package principal;

import java.util.Scanner;

public class Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner media = new Scanner(System.in);
		
		double bi1;
		double bi2;
		double bi3;
		double bi4;
		
		
		System.out.println("Nota do primeiro bimestre: ");
		bi1 = media.nextDouble();
		
		System.out.println("Nota do segundo bimestre: ");
		bi2 = media.nextDouble();

		System.out.println("Nota do terceiro bimestre: ");
		bi3 = media.nextDouble();
		
		System.out.println("Nota do quarto bimestre: ");
		bi4 = media.nextDouble();
		
		double somaDosBi = bi1 + bi2 + bi3 + bi4;
		
		double mediaDosBi = somaDosBi / 4;
		
		System.out.println("A média do bimestre é: " + mediaDosBi);
		

	}

}
